import { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import UserAccessType from '../../../contracts/Enums/UserAccessType'
import CreateUserService from '../../Services/CreateUserService'
import EmailConfirmationService from '../../Services/EmailConfirmationService'
import ValidateDataService from '../../Services/ValidateDataService'
import DependentValidator from '../../Validators/DependentValidator'

export default class DependentController {
  public async create({ request, response }: HttpContextContract) {
    // Valida os dados do request
    await request.validate(DependentValidator)

    const person = request.body()

    // Busca o dependente
    const dependent = await ValidateDataService.findByCPF(person.cpf)

    if (!dependent) {
      return response.status(400).json({ message: 'CPF não encontrado' })
    }

    // Verifica se o usuário já está cadastrado
    await ValidateDataService.checkRegisteredUser(dependent.id)

    // Verifica se o RE existe
    const contributor = await ValidateDataService.findByRE(person.reDoContribuinte)

    // Verifica se o dependente é um beneficiario
    await ValidateDataService.isBeneficiary(dependent.id, contributor.id)

    // Verifica se o provedor informado é um contribuinte
    await ValidateDataService.isContribuinte(contributor.id)

    // Verifica se o dependente está ativo
    await ValidateDataService.isActived(dependent.id)

    // Verifica se o RG é valido
    await ValidateDataService.isValidRG(person.rg)

    // Verifica se o e-mail já está cadastrado
    await ValidateDataService.isUsedEmail(person.email)

    // Verifica se o e-mail é valido
    await ValidateDataService.isValidEmail(person.email)

    // Verifica se a data de nascimento é valida
    await ValidateDataService.isValidBirthdate(person.dtnascimento, dependent.dtnascimento)

    // Cria um usuário
    const user = await CreateUserService.Create({
      id_pessoa: dependent.id,
      id_provedor: contributor.id,
      cpf: person.cpf,
      email: person.email,
      senha: person.senha,
      categoria: UserAccessType.DEPENDENT,
    })

    // Envia um e-mail com o link de validação da conta
    await EmailConfirmationService.execute(person.email)

    return response.json(user)
  }
}

enum UserAccessType {
  CONTRIBUTOR = 'CONTRIBUINTE',
  DEPENDENT = 'DEPENDENTE',
  PENSIONER = 'PENSIONISTA',
}

export default UserAccessType

import UsuarioPortalCBPM from '../Models/UsuarioPortalCBPM'

interface PolicialData {
  id_pessoa: number
  id_provedor: number
  cpf: string
  email: string
  senha: string
  categoria: string
}

export default class CreateUserService {
  public static async Create({
    id_pessoa,
    id_provedor,
    cpf,
    email,
    senha,
    categoria,
  }: PolicialData): Promise<UsuarioPortalCBPM> {
    const usuario = await UsuarioPortalCBPM.create({
      id_pessoa,
      id_provedor,
      cpf,
      email,
      senha,
      conta_ativada: false,
      categoria,
    })

    return usuario
  }
}

import Route from '@ioc:Adonis/Core/Route'
import EmailConfirmation from '../Mailers/EmailConfirmation'

export default class EmailConfirmationService {
  public static async execute(email: string) {
    const url = Route.makeSignedUrl('verifyEmail', { params: { email: email }, expiresIn: '60m' })

    const confirmationUrl = `http://${process.env.APPLICATION_URL}${url}`

    const confirmationData = { confirmationUrl, email }

    await new EmailConfirmation(confirmationData).sendLater()
  }
}

import { BaseMailer, MessageContract } from '@ioc:Adonis/Addons/Mail'
import View from '@ioc:Adonis/Core/View'
import mjml from 'mjml'

type ConfirmationData = {
  confirmationUrl: string
  email: string
}

export default class EmailConfirmation extends BaseMailer {
  constructor(private data: ConfirmationData) {
    super()
  }

  public html = mjml(
    View.renderSync('emails/email-confirmation', { url: this.data.confirmationUrl })
  ).html

  public prepare(message: MessageContract) {
    message
      .subject('Confirmação da conta Portal CBPM')
      .from('tiamh@cbpm.sp.gov.br', 'TI AMH')
      .to(`${this.data.email}`)
      .html(this.html)
  }
}

